import React, { useState } from "react";
import { Button, FormControl, FormHelperText, Grid, InputLabel, MenuItem, Select, TextField, withStyles } from "@material-ui/core";
import useForm from "./useForm";

const styles = theme => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            minWidth: 230
        },
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 230
    },
    smMargin: {
        margin: theme.spacing(1)
    }
})

const initialFieldValues = {
    fullName: '',
    mobile: '',
    email: '',
    age: '',
    bloodGroup: '',
    address: ''
}

const DCandidateForm = ({classes,...props}) => {

    const validate = () => {
        let temp = {}
        temp.fullName = values.fullName ? "":"This field is required!"
        temp.mobile = values.mobile ? "":"This field is required!"
        temp.bloodGroupd = values.bloodGroup ? "":"This field is required!"
        temp.email = (/^$|.+@.+..+/).test(values.email) ? "": "Email is not valid!"
        setErrors({
            ...temp
        })

        return Object.values(temp).every(x => x=="")
    }

    const {
        values, 
        setValues,
        errors,
        setErrors,
        handleInputChange
    } = useForm(initialFieldValues)

    const inputLabel = React.useRef(null);
    const [labelWidth, setLabelWidth] = React.useState(0);
    React.useEffect(() => {
        setLabelWidth(inputLabel.current.offsetWidth);
    }, []);

    const handleSubmit = e => {
        e.preventDefault()
        console.log(values)

        if (validate()) {
            window.alert("Validation succeeded")
        }
    }

    return (
        <form autoComplete="off" noValidate className={classes.root} onSubmit={handleSubmit}>
            <Grid container>
                <Grid item xs={6}>
                    <TextField 
                    name="fullName"
                    variant="outlined"
                    label="Full Name"
                    value={values.fullName}
                    onChange={handleInputChange}
                    //error={true}
                    //helperText={errors.fullName}
                    {...(errors.fullName && { error: true, helperText: errors.fullName })}
                    />
                    <TextField 
                    name="email"
                    variant="outlined"
                    label="Email"
                    value={values.email}
                    onChange={handleInputChange}
                    {...(errors.email && { error: true })}
                    />
                    <FormControl variant="outlined" className={classes.formControl}
                    {...(errors.bloodGroup && { error: true })}
                    >
                        <InputLabel ref={inputLabel}>Blood Group</InputLabel>
                        <Select
                        name="bloodGroup"
                        value={values.bloodGroup}
                        onChange={handleInputChange}
                        labelWidth={labelWidth}
                        >
                            <MenuItem value="">Select blood group</MenuItem>
                            <MenuItem value="AB+">AB+</MenuItem>
                            <MenuItem value="AB-">AB-</MenuItem>
                            <MenuItem value="0+">0+</MenuItem>
                            <MenuItem value="0-">0-</MenuItem>
                            <MenuItem value="A+">A+</MenuItem>
                            <MenuItem value="A-">A-</MenuItem>
                        </Select>
                        {errors.bloodGroup && <FormHelperText>{errors.bloodGroup}</FormHelperText>}
                    </FormControl>
                </Grid>
                <Grid item xs={6}>
                    <TextField 
                    name="mobile"
                    variant="outlined"
                    label="Mobile"
                    value={values.mobile}
                    onChange={handleInputChange}
                    {...(errors.mobile && { error: true, helperText: errors.mobile })}
                    />
                    <TextField 
                    name="age"
                    variant="outlined"
                    label="Age"
                    value={values.age}
                    onChange={handleInputChange}
                    />
                    <TextField 
                    name="address"
                    variant="outlined"
                    label="Address"
                    value={values.address}
                    onChange={handleInputChange}
                    />
                    <div>
                        <Button
                        variant="contained"
                        color="primary"
                        type="submit"
                        className={classes.smMargin}
                        >
                            Submit
                        </Button>
                        <Button
                        variant="contained"
                        className={classes.smMargin}
                        >
                            Resest
                        </Button>
                    </div>
                </Grid>

            </Grid>

        </form>
    )
}
 
export default withStyles(styles)(DCandidateForm);
